<span class="line"><span style="color:#FFCB6B;">aptitude</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">remove</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">pkgname</span><span style="color:#EEFFFF;">    </span><span style="color:#545454;font-style:italic;"># 删除包</span></span>
