import{_ as s,c as a,o as n,a as l}from"./app.82736834.js";const D=JSON.parse('{"title":"clock","description":"","frontmatter":{},"headers":[{"level":2,"title":"补充说明","slug":"补充说明","link":"#补充说明","children":[{"level":3,"title":"语法","slug":"语法","link":"#语法","children":[]},{"level":3,"title":"选项","slug":"选项","link":"#选项","children":[]},{"level":3,"title":"实例","slug":"实例","link":"#实例","children":[]}]}],"relativePath":"linux/command/pages/clock.md","lastUpdated":1666509709000}'),e={name:"linux/command/pages/clock.md"},p=l(`<h1 id="clock" tabindex="-1">clock <a class="header-anchor" href="#clock" aria-hidden="true">#</a></h1><p>用于调整 RTC 时间</p><h2 id="补充说明" tabindex="-1">补充说明 <a class="header-anchor" href="#补充说明" aria-hidden="true">#</a></h2><p><strong>clock命令</strong>用于调整 RTC 时间。 RTC 是电脑内建的硬件时间，执行这项指令可以显示现在时刻，调整硬件时钟的时间，将系统时间设成与硬件时钟之时间一致，或是把系统时间回存到硬件时钟。</p><h3 id="语法" tabindex="-1">语法 <a class="header-anchor" href="#语法" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">clock</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">--adjust</span><span style="color:#89DDFF;">][</span><span style="color:#EEFFFF;">--debug</span><span style="color:#89DDFF;">][</span><span style="color:#EEFFFF;">--directisa</span><span style="color:#89DDFF;">][</span><span style="color:#EEFFFF;">--getepoch</span><span style="color:#89DDFF;">][</span><span style="color:#EEFFFF;">--hctosys</span><span style="color:#89DDFF;">][</span><span style="color:#EEFFFF;">--set --date</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">&lt;日期时间&gt;</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">]</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">--setepoch --epoch</span><span style="color:#89DDFF;">=&lt;</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">&gt;][</span><span style="color:#EEFFFF;">--show</span><span style="color:#89DDFF;">][</span><span style="color:#EEFFFF;">--systohc</span><span style="color:#89DDFF;">][</span><span style="color:#EEFFFF;">--test</span><span style="color:#89DDFF;">][</span><span style="color:#EEFFFF;">--utc</span><span style="color:#89DDFF;">][</span><span style="color:#EEFFFF;">--version</span><span style="color:#89DDFF;">]</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h3 id="选项" tabindex="-1">选项 <a class="header-anchor" href="#选项" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">--adjust</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">第一次使用</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">--set</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">或</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">--systohc</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">参数设置硬件时钟，会在/etc目录下产生一个名称为adjtime的文件。当再次使用这两个参数调整硬件时钟，此文件便会记录两次调整间之差异，日后执行clock指令加上</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">--adjust</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">参数时，程序会自动根</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">据记录文件的数值差异，计算出平均值，自动调整硬件时钟的时间。</span></span>
<span class="line"><span style="color:#FFCB6B;">--debug</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">详细显示指令执行过程，便于排错或了解程序执行的情形。</span></span>
<span class="line"><span style="color:#FFCB6B;">--directisa</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">告诉clock指令不要通过/dev/rtc设备文件，直接对硬件时钟进行存取。这个参数适用于仅有ISA总线结构的老式电脑。</span></span>
<span class="line"><span style="color:#FFCB6B;">--getepoch</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">把系统核心内的硬件时钟新时代数值，呈现到标准输出设备。</span></span>
<span class="line"><span style="color:#FFCB6B;">--hctosys</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">Hardware</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">Clock</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">to</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">System</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">Time，把系统时间设成和硬件时钟一致。由于这个动作将会造成系统全面更新文件的存取时间，所以最好在系统启动时就执行它。</span></span>
<span class="line"><span style="color:#FFCB6B;">--</span><span style="color:#82AAFF;">set</span><span style="color:#FFCB6B;">--date</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">设置硬件时钟的日期和时间。</span></span>
<span class="line"><span style="color:#FFCB6B;">--setepoch--epoch=&lt;年份&gt;</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">设置系统核心之硬件时钟的新时代数值，年份以四位树字表示。</span></span>
<span class="line"><span style="color:#FFCB6B;">--show</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">读取硬件时钟的时间，并将其呈现至标准输出设备。</span></span>
<span class="line"><span style="color:#FFCB6B;">--systohc</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">System</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">Time</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">to</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">Hardware</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">Clock，将系统时间存回硬件时钟内。</span></span>
<span class="line"><span style="color:#FFCB6B;">--</span><span style="color:#82AAFF;">test</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">仅作测试，并不真的将时间写入硬件时钟或系统时间。</span></span>
<span class="line"><span style="color:#FFCB6B;">--utc</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">把硬件时钟上的时间时为CUT，有时也称为UTC或UCT。</span></span>
<span class="line"><span style="color:#FFCB6B;">--version</span><span style="color:#EEFFFF;"> 　</span><span style="color:#C3E88D;">显示版本信息。</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br></div></div><h3 id="实例" tabindex="-1">实例 <a class="header-anchor" href="#实例" aria-hidden="true">#</a></h3><p>获取当前的时间</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">clock</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 获取当前的时间</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>显示UTC时间</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">clock</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-utc</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;">#显示UTC时间</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div>`,13),o=[p];function c(t,r,F,i,y,E){return n(),a("div",null,o)}const h=s(e,[["render",c]]);export{D as __pageData,h as default};
