<span class="line"><span style="color:#EEFFFF;">host</span><span style="color:#89DDFF;">=</span><span style="color:#C3E88D;">202.38.64.9</span><span style="color:#EEFFFF;"> rtt</span><span style="color:#89DDFF;">=</span><span style="color:#F78C6C;">750</span><span style="color:#89DDFF;">(</span><span style="color:#F78C6C;">187</span><span style="color:#89DDFF;">)</span><span style="color:#C3E88D;">ms/0ms</span><span style="color:#EEFFFF;"> delta</span><span style="color:#89DDFF;">=</span><span style="color:#C3E88D;">-857517ms/-857517ms</span><span style="color:#EEFFFF;"> Wed Dec </span><span style="color:#F78C6C;">17</span><span style="color:#EEFFFF;"> </span><span style="color:#F78C6C;">11</span><span style="color:#EEFFFF;">:</span><span style="color:#F78C6C;">28</span><span style="color:#EEFFFF;">:</span><span style="color:#F78C6C;">35</span><span style="color:#EEFFFF;"> </span><span style="color:#F78C6C;">2008</span></span>
