import{_ as s,c as n,o as a,a as l}from"./app.82736834.js";const b=JSON.parse('{"title":"csplit","description":"","frontmatter":{},"headers":[{"level":2,"title":"补充说明","slug":"补充说明","link":"#补充说明","children":[{"level":3,"title":"语法","slug":"语法","link":"#语法","children":[]},{"level":3,"title":"选项","slug":"选项","link":"#选项","children":[]},{"level":3,"title":"参数","slug":"参数","link":"#参数","children":[]},{"level":3,"title":"实例","slug":"实例","link":"#实例","children":[]}]}],"relativePath":"linux/command/pages/csplit.md","lastUpdated":1666509709000}'),e={name:"linux/command/pages/csplit.md"},p=l(`<h1 id="csplit" tabindex="-1">csplit <a class="header-anchor" href="#csplit" aria-hidden="true">#</a></h1><p>将一个大文件分割成小的碎片文件</p><h2 id="补充说明" tabindex="-1">补充说明 <a class="header-anchor" href="#补充说明" aria-hidden="true">#</a></h2><p><strong>csplit命令</strong> 用于将一个大文件分割成小的碎片，并且将分割后的每个碎片保存成一个文件。碎片文件的命名类似“xx00”，“xx01”。csplit命令是split的一个变体，split只能够根据文件大小或行数来分割，但csplit能够根据文件本身特点来分割文件。</p><h3 id="语法" tabindex="-1">语法 <a class="header-anchor" href="#语法" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">csplit(选项</span><span style="color:#EEFFFF;">)</span><span style="color:#89DDFF;">(</span><span style="color:#FFCB6B;">参数</span><span style="color:#89DDFF;">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="选项" tabindex="-1">选项 <a class="header-anchor" href="#选项" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">-b&lt;输出格式&gt;或--suffix-format=&lt;输出格式&gt;：预设的输出格式其文件名称为xx00，xx01等，用户可以通过改变&lt;输出格式&gt;来改变输出的文件名；</span></span>
<span class="line"><span style="color:#FFCB6B;">-f&lt;输出字首字符串&gt;或--prefix=&lt;输出字首字符串&gt;：预设的输出字首字符串其文件名为xx00，xx01等，如果制定输出字首字符串为“hello”，则输出的文件名称会变成hello00，hello、01.</span><span style="color:#82AAFF;">.....</span></span>
<span class="line"><span style="color:#FFCB6B;">-k或--keep-files：保留文件，就算发生错误或中断执行，与不能删除已经输出保存的文件；</span></span>
<span class="line"><span style="color:#FFCB6B;">-n&lt;输出文件名位数&gt;或--digits=&lt;输出文件名位数&gt;：预设的输出文件名位数其文件名称为xx00，xx01.</span><span style="color:#82AAFF;">....</span><span style="color:#FFCB6B;">.如果用户指定输出文件名位数为“3”，则输出的文件名称会变成xx000，xx001等；</span></span>
<span class="line"><span style="color:#FFCB6B;">-q或-s或--quiet或——silent：不显示指令执行过程；</span></span>
<span class="line"><span style="color:#FFCB6B;">-z或--elide-empty-files：删除长度为0</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">Byte文件。</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><h3 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-hidden="true">#</a></h3><ul><li>文件：指定要分割的原文件；</li><li>模式：指定要分割文件时的匹配模式。</li></ul><h3 id="实例" tabindex="-1">实例 <a class="header-anchor" href="#实例" aria-hidden="true">#</a></h3><p>示例测试文件 server.log</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">cat</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">server.log</span></span>
<span class="line"><span style="color:#FFCB6B;">SERVER-1</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">con</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.1 suc</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">con</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.2 fai</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">dis</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.3 pen</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">con</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.4 suc</span></span>
<span class="line"><span style="color:#FFCB6B;">SERVER-2</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">con</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.5 suc</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">con</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.6 fai</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">dis</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.7 pen</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">con</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.8 suc</span></span>
<span class="line"><span style="color:#FFCB6B;">SERVER-3</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">con</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.9 suc</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">con</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.10 fai</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">dis</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.11 pen</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">con</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"> 10.10.10.12 suc</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br></div></div><p>需要将server.log分割成server1.log、server2.log、server3.log，这些文件的内容分别取自原文件中不同的SERVER部分：</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">root@localhost split</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"># csplit server.log /SERVER/ -n2 -s </span><span style="color:#89DDFF;">{*}</span><span style="color:#EEFFFF;"> -f server -b</span><span style="color:#89DDFF;"> &quot;</span><span style="color:#C3E88D;">%02d.log</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">;</span><span style="color:#EEFFFF;"> </span><span style="color:#FFCB6B;">rm</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">server00.log</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">root@localhost split</span><span style="color:#89DDFF;">]</span><span style="color:#EEFFFF;"># ls</span></span>
<span class="line"><span style="color:#FFCB6B;">server01.log</span><span style="color:#EEFFFF;">  </span><span style="color:#C3E88D;">server02.log</span><span style="color:#EEFFFF;">  </span><span style="color:#C3E88D;">server03.log</span><span style="color:#EEFFFF;">  </span><span style="color:#C3E88D;">server.log</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p><strong>命令详细说明：</strong></p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">/[正则表达式</span><span style="color:#EEFFFF;">]/   </span><span style="color:#545454;font-style:italic;">#匹配文本样式，比如/SERVER/，从第一行到包含SERVER的匹配行。</span></span>
<span class="line"><span style="color:#89DDFF;">{*}</span><span style="color:#EEFFFF;">     </span><span style="color:#545454;font-style:italic;">#表示根据匹配重复执行分割，直到文件尾停止，使用{整数}的形式指定分割执行的次数。</span></span>
<span class="line"><span style="color:#FFCB6B;">-s</span><span style="color:#EEFFFF;">      </span><span style="color:#545454;font-style:italic;">#静默模式，不打印其他信息。</span></span>
<span class="line"><span style="color:#FFCB6B;">-n</span><span style="color:#EEFFFF;">      </span><span style="color:#545454;font-style:italic;">#指定分割后的文件名后缀的数字个数。比如01、02、03等。</span></span>
<span class="line"><span style="color:#FFCB6B;">-f</span><span style="color:#EEFFFF;">      </span><span style="color:#545454;font-style:italic;">#指定分割后的文件名前缀。</span></span>
<span class="line"><span style="color:#FFCB6B;">-b</span><span style="color:#EEFFFF;">      </span><span style="color:#545454;font-style:italic;">#指定后缀格式。比如%02d.log，类似于C语言中的printf参数格式。</span></span>
<span class="line"><span style="color:#FFCB6B;">rm</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">server00.log</span><span style="color:#EEFFFF;">    </span><span style="color:#545454;font-style:italic;">#是删除第一个文件，因为分割后的的第一个文件没有内容，匹配的单词就位于文件的第一行中。</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div>`,17),o=[p];function r(c,F,t,i,y,d){return a(),n("div",null,o)}const u=s(e,[["render",r]]);export{b as __pageData,u as default};
