import{_ as s,c as a,o as n,a as e}from"./app.82736834.js";const y=JSON.parse('{"title":"ftpshut","description":"","frontmatter":{},"headers":[{"level":2,"title":"补充说明","slug":"补充说明","link":"#补充说明","children":[]}],"relativePath":"linux/command/pages/ftpshut.md","lastUpdated":1666509709000}'),l={name:"linux/command/pages/ftpshut.md"},t=e(`<h1 id="ftpshut" tabindex="-1">ftpshut <a class="header-anchor" href="#ftpshut" aria-hidden="true">#</a></h1><p>在指定的时间关闭FTP服务器</p><h2 id="补充说明" tabindex="-1">补充说明 <a class="header-anchor" href="#补充说明" aria-hidden="true">#</a></h2><p>功能说明：在指定的时间关闭ftp服务器。本指令提供系统管理者在设置的时间关闭FTP服务器，且能在关闭之前发出警告信息通知用户。关闭时间若设置后为&quot;none&quot;，则会马上关闭服务器。如果采 用&quot;+30&quot;的方式来设置表示服务器在30分钟之后关闭。依次类推，假设使用&quot;1130&quot;的格式则代表服务器会在每日的11时30分关闭，时间格式为24 小时制。FTP服务器关闭后，在/etc目录下会产生一个名称为shutmsg的文件，把它删除后即可再度启动FTP服务器的功能。</p><p>语法：</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ftpshut</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">[</span><span style="color:#EEFFFF;">-d</span><span style="color:#89DDFF;">&lt;</span><span style="color:#EEFFFF;">分钟</span><span style="color:#89DDFF;">&gt;][</span><span style="color:#EEFFFF;">-l</span><span style="color:#89DDFF;">&lt;</span><span style="color:#EEFFFF;">分钟</span><span style="color:#89DDFF;">&gt;][</span><span style="color:#EEFFFF;">关闭时间</span><span style="color:#89DDFF;">][</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">警告信息</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">]</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>参数：</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">-d&lt;分钟&gt;</span><span style="color:#EEFFFF;">   </span><span style="color:#C3E88D;">切断所有FTP连线时间。</span></span>
<span class="line"><span style="color:#FFCB6B;">-l&lt;分钟&gt;</span><span style="color:#EEFFFF;">   </span><span style="color:#C3E88D;">停止接受FTP登入的时间。</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div>`,8),p=[t];function o(r,c,F,i,d,u){return n(),a("div",null,p)}const _=s(l,[["render",o]]);export{y as __pageData,_ as default};
