import{_ as e,c as a,o as s,a as n}from"./app.82736834.js";const x=JSON.parse('{"title":"kexec","description":"","frontmatter":{},"headers":[{"level":2,"title":"补充说明","slug":"补充说明","link":"#补充说明","children":[{"level":3,"title":"语法","slug":"语法","link":"#语法","children":[]},{"level":3,"title":"选项","slug":"选项","link":"#选项","children":[]}]}],"relativePath":"linux/command/pages/kexec.md","lastUpdated":1666509709000}'),l={name:"linux/command/pages/kexec.md"},r=n(`<h1 id="kexec" tabindex="-1">kexec <a class="header-anchor" href="#kexec" aria-hidden="true">#</a></h1><p>从当前正在运行的内核引导到一个新内核</p><h2 id="补充说明" tabindex="-1">补充说明 <a class="header-anchor" href="#补充说明" aria-hidden="true">#</a></h2><p><strong>kexec命令</strong> 是Linux内核的一个补丁，让您可以从当前正在运行的内核直接引导到一个新内核。在上面描述的引导序列中，kexec跳过了整个引导装载程序阶段（第一部分）并直接跳转到我们希望引导到的内核。不再有硬件的重启，不再有固件操作，不再涉及引导装载程序。完全避开了引导序列中最弱的一环 -- 固件。这一功能部件带来的最大益处在于，系统现在可以极其快速地重新启动。</p><p><strong>kexec的好处：</strong> 要求高可用性的系统，以及需要不断重新启动系统的内核开发人员，都将受益于kexec。因为 kexec跳过了系统重新启动过程中最耗时的部分（也就是固件初始化硬件设备的阶段），所以重新启动变得非常快，可用性得到了提高。</p><h3 id="语法" tabindex="-1">语法 <a class="header-anchor" href="#语法" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">kexec(选项</span><span style="color:#EEFFFF;">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="选项" tabindex="-1">选项 <a class="header-anchor" href="#选项" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">-l：指定内核映像文件；</span></span>
<span class="line"><span style="color:#FFCB6B;">-e：允许当前被加载的内核；</span></span>
<span class="line"><span style="color:#FFCB6B;">-f：强制立即调用系统调用“kexec”，而不调用“shutdown”；</span></span>
<span class="line"><span style="color:#FFCB6B;">-t：指定新内核的类型；</span></span>
<span class="line"><span style="color:#FFCB6B;">-u：卸载当前的kexec目标内核。</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div>`,9),c=[r];function i(t,p,d,o,h,u){return s(),a("div",null,c)}const _=e(l,[["render",i]]);export{x as __pageData,_ as default};
