<span class="line"><span style="color:#FFCB6B;">-o</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">suid、-o</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">nosuid：</span></span>
