import{_ as s,c as n,o as a,a as l}from"./app.82736834.js";const B=JSON.parse('{"title":"patch","description":"","frontmatter":{},"headers":[{"level":2,"title":"补充说明","slug":"补充说明","link":"#补充说明","children":[{"level":3,"title":"语法","slug":"语法","link":"#语法","children":[]},{"level":3,"title":"选项","slug":"选项","link":"#选项","children":[]},{"level":3,"title":"参数","slug":"参数","link":"#参数","children":[]}]}],"relativePath":"linux/command/pages/patch.md","lastUpdated":1666509709000}'),e={name:"linux/command/pages/patch.md"},p=l(`<h1 id="patch" tabindex="-1">patch <a class="header-anchor" href="#patch" aria-hidden="true">#</a></h1><p>为开放源代码软件安装补丁程序</p><h2 id="补充说明" tabindex="-1">补充说明 <a class="header-anchor" href="#补充说明" aria-hidden="true">#</a></h2><p><strong>patch命令</strong> 被用于为开放源代码软件安装补丁程序。让用户利用设置修补文件的方式，修改，更新原始文件。如果一次仅修改一个文件，可直接在命令列中下达指令依序执行。如果配合修补文件的方式则能一次修补大批文件，这也是Linux系统核心的升级方法之一。</p><h3 id="语法" tabindex="-1">语法 <a class="header-anchor" href="#语法" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">patch(选项</span><span style="color:#EEFFFF;">)</span><span style="color:#89DDFF;">(</span><span style="color:#FFCB6B;">参数</span><span style="color:#89DDFF;">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="选项" tabindex="-1">选项 <a class="header-anchor" href="#选项" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">-b或--backup：备份每一个原始文件；</span></span>
<span class="line"><span style="color:#FFCB6B;">-B&lt;备份字首字符串&gt;或--prefix=&lt;备份字首字符串&gt;：设置文件备份时，附加在文件名称前面的字首字符串，该字符串可以是路径名称；</span></span>
<span class="line"><span style="color:#FFCB6B;">-c或--context：把修补数据解译成关联性的差异；</span></span>
<span class="line"><span style="color:#FFCB6B;">-d&lt;工作目录&gt;或--directory=&lt;工作目录&gt;：设置工作目录；</span></span>
<span class="line"><span style="color:#FFCB6B;">-D&lt;标示符号&gt;或--ifdef=&lt;标示符号&gt;：用指定的符号把改变的地方标示出来；</span></span>
<span class="line"><span style="color:#FFCB6B;">-e或--ed：把修补数据解译成ed指令可用的叙述文件；</span></span>
<span class="line"><span style="color:#FFCB6B;">-E或--remove-empty-files：若修补过后输出的文件其内容是一片空白，则移除该文件；</span></span>
<span class="line"><span style="color:#FFCB6B;">-f或--force：此参数的效果和指定&quot;-t&quot;参数类似，但会假设修补数据的版本为新版本；</span></span>
<span class="line"><span style="color:#FFCB6B;">-F&lt;监别列数&gt;或--fuzz&lt;监别列数&gt;：设置监别列数的最大值；</span></span>
<span class="line"><span style="color:#FFCB6B;">-g&lt;控制数值&gt;或--get=&lt;控制数值&gt;：设置以RSC或SCCS控制修补作业；</span></span>
<span class="line"><span style="color:#FFCB6B;">-i&lt;修补文件&gt;或--input=&lt;修补文件&gt;：读取指定的修补问家你；</span></span>
<span class="line"><span style="color:#FFCB6B;">-l或--ignore-whitespace：忽略修补数据与输入数据的跳格，空格字符；</span></span>
<span class="line"><span style="color:#FFCB6B;">-n或--normal：把修补数据解译成一般性的差异；</span></span>
<span class="line"><span style="color:#FFCB6B;">-N或--forward：忽略修补的数据较原始文件的版本更旧，或该版本的修补数据已使</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">用过；</span></span>
<span class="line"><span style="color:#FFCB6B;">-o&lt;输出文件&gt;或--output=&lt;输出文件&gt;：设置输出文件的名称，修补过的文件会以该名称存放；</span></span>
<span class="line"><span style="color:#FFCB6B;">-p&lt;剥离层级&gt;或--strip=&lt;剥离层级&gt;：设置欲剥离几层路径名称；</span></span>
<span class="line"><span style="color:#FFCB6B;">-f&lt;拒绝文件&gt;或--reject-file=&lt;拒绝文件&gt;：设置保存拒绝修补相关信息的文件名称，预设的文件名称为.rej；</span></span>
<span class="line"><span style="color:#FFCB6B;">-R或--reverse：假设修补数据是由新旧文件交换位置而产生；</span></span>
<span class="line"><span style="color:#FFCB6B;">-s或--quiet或--silent：不显示指令执行过程，除非发生错误；</span></span>
<span class="line"><span style="color:#FFCB6B;">-t或--batch：自动略过错误，不询问任何问题；</span></span>
<span class="line"><span style="color:#FFCB6B;">-T或--</span><span style="color:#82AAFF;">set</span><span style="color:#FFCB6B;">-time：此参数的效果和指定&quot;-Z&quot;参数类似，但以本地时间为主；</span></span>
<span class="line"><span style="color:#FFCB6B;">-u或--unified：把修补数据解译成一致化的差异；</span></span>
<span class="line"><span style="color:#FFCB6B;">-v或--version：显示版本信息；</span></span>
<span class="line"><span style="color:#FFCB6B;">-V&lt;备份方式&gt;或--version-control=&lt;备份方式&gt;：用&quot;-b&quot;参数备份目标文件后，备份文件的字尾会被加上一个备份字符串，这个字符串不仅可用&quot;-z&quot;参数变更，当使用&quot;-V&quot;参数指定不同备份方式时，也会产生不同字尾的备份字符串；</span></span>
<span class="line"><span style="color:#FFCB6B;">-Y&lt;备份字首字符串&gt;或--basename-prefix=--&lt;备份字首字符串&gt;：设置文件备份时，附加在文件基本名称开头的字首字符串；</span></span>
<span class="line"><span style="color:#FFCB6B;">-z&lt;备份字尾字符串&gt;或--suffix=&lt;备份字尾字符串&gt;：此参数的效果和指定&quot;-B&quot;参数类似，差别在于修补作业使用的路径与文件名若为src/linux/fs/super.c，加上&quot;backup/&quot;字符串后，文件super.c会备份于/src/linux/fs/backup目录里；</span></span>
<span class="line"><span style="color:#FFCB6B;">-Z或--</span><span style="color:#82AAFF;">set</span><span style="color:#FFCB6B;">-utc：把修补过的文件更改，存取时间设为UTC；</span></span>
<span class="line"><span style="color:#FFCB6B;">--backup-if-mismatch：在修补数据不完全吻合，且没有刻意指定要备份文件时，才备份文件；</span></span>
<span class="line"><span style="color:#FFCB6B;">--binary：以二进制模式读写数据，而不通过标准输出设备；</span></span>
<span class="line"><span style="color:#FFCB6B;">--help：在线帮助；</span></span>
<span class="line"><span style="color:#FFCB6B;">--nobackup-if-mismatch：在修补数据不完全吻合，且没有刻意指定要备份文件时，不要备份文件；</span></span>
<span class="line"><span style="color:#FFCB6B;">--verbose：详细显示指令的执行过程。</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br><span class="line-number">29</span><br><span class="line-number">30</span><br><span class="line-number">31</span><br><span class="line-number">32</span><br></div></div><h3 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-hidden="true">#</a></h3><ul><li>原文件：指定需要打补丁的原始文件；</li><li>补丁文件：指定补丁文件。</li></ul>`,10),t=[p];function r(c,i,o,u,b,F){return a(),n("div",null,t)}const m=s(e,[["render",r]]);export{B as __pageData,m as default};
