import{_ as s,c as n,o as a,a as l}from"./app.82736834.js";const u=JSON.parse('{"title":"pr","description":"","frontmatter":{},"headers":[{"level":2,"title":"补充说明","slug":"补充说明","link":"#补充说明","children":[{"level":3,"title":"语法","slug":"语法","link":"#语法","children":[]},{"level":3,"title":"选项","slug":"选项","link":"#选项","children":[]},{"level":3,"title":"参数","slug":"参数","link":"#参数","children":[]}]}],"relativePath":"linux/command/pages/pr.md","lastUpdated":1666509709000}'),p={name:"linux/command/pages/pr.md"},e=l(`<h1 id="pr" tabindex="-1">pr <a class="header-anchor" href="#pr" aria-hidden="true">#</a></h1><p>将文本文件转换成适合打印的格式</p><h2 id="补充说明" tabindex="-1">补充说明 <a class="header-anchor" href="#补充说明" aria-hidden="true">#</a></h2><p><strong>pr命令</strong> 用来将文本文件转换成适合打印的格式，它可以把较大的文件分割成多个页面进行打印，并为每个页面添加标题。</p><h3 id="语法" tabindex="-1">语法 <a class="header-anchor" href="#语法" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">pr(选项</span><span style="color:#EEFFFF;">)</span><span style="color:#89DDFF;">(</span><span style="color:#FFCB6B;">参数</span><span style="color:#89DDFF;">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="选项" tabindex="-1">选项 <a class="header-anchor" href="#选项" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">-e&lt;制表符[宽度</span><span style="color:#EEFFFF;">]</span><span style="color:#89DDFF;">&gt;(</span><span style="color:#FFCB6B;">--expand-tabs=&lt;制表符[宽度</span><span style="color:#C3E88D;">]</span><span style="color:#89DDFF;">&gt;)</span><span style="color:#EEFFFF;">：将制表符（或制表符）转换为空格.如果指定了宽度，则将制表符转换为宽度字符，默认为8</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#FFCB6B;">-h&lt;标题&gt;：为页指定标题；</span></span>
<span class="line"><span style="color:#FFCB6B;">-i&lt;out-tab-char[out-tab-width&gt;(--output-tabs&lt;out-tab-char[out-tab-width</span><span style="color:#EEFFFF;">]</span><span style="color:#89DDFF;">&gt;</span><span style="color:#EEFFFF;">)：在输出中用制表符替换空格，可以指定替代制表符</span><span style="color:#89DDFF;">(</span><span style="color:#FFCB6B;">默认为制表符</span><span style="color:#89DDFF;">)</span><span style="color:#EEFFFF;">和宽度</span><span style="color:#89DDFF;">(</span><span style="color:#FFCB6B;">默认为8</span><span style="color:#89DDFF;">)</span><span style="color:#EEFFFF;">；</span></span>
<span class="line"><span style="color:#FFCB6B;">-l&lt;行数&gt;：指定每页的行数；</span></span>
<span class="line"><span style="color:#FFCB6B;">-n&lt;分隔符[数字</span><span style="color:#EEFFFF;">]</span><span style="color:#89DDFF;">&gt;</span><span style="color:#EEFFFF;">：对列进行编号，或者使用 -m 选项对行进行编号，将分隔符附加到每个数字默认为制表符并限制数字的大小，默认为5；</span></span>
<span class="line"><span style="color:#FFCB6B;">-o&lt;宽度&gt;：为左边距设置宽度；</span></span>
<span class="line"><span style="color:#FFCB6B;">-s&lt;分隔符&gt;(--separator&lt;分隔符&gt;</span><span style="color:#EEFFFF;">)：使用指定的分隔符（默认为制表符）而不是空格分隔列；</span></span>
<span class="line"><span style="color:#FFCB6B;">-S&lt;字符串&gt;(--sep-string&lt;字符串&gt;</span><span style="color:#EEFFFF;">)：使用指定的字符串（默认为-J的制表符）或者是一个空格分隔列；</span></span>
<span class="line"><span style="color:#FFCB6B;">-w&lt;页面宽度&gt;：设置页面宽度以进行多列输出，默认为72；</span></span>
<span class="line"><span style="color:#FFCB6B;">-W&lt;页面宽度&gt;：设置页面宽度为固定值，默认为72；</span></span>
<span class="line"><span style="color:#FFCB6B;">-J(--join-lines</span><span style="color:#EEFFFF;">)：合并整行，忽略 -W 如果设置</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#FFCB6B;">-num_cols：num_cols是一个整数，为一页有n行的文件打印指定num_cols列；</span></span>
<span class="line"><span style="color:#FFCB6B;">-m(--merge</span><span style="color:#EEFFFF;">)：打印所有文件，每列一个文件。</span></span>
<span class="line"><span style="color:#FFCB6B;">-f(-F</span><span style="color:#EEFFFF;">)</span><span style="color:#89DDFF;">(</span><span style="color:#FFCB6B;">--form-feed</span><span style="color:#89DDFF;">)</span><span style="color:#EEFFFF;">：用换页符替代换行符；</span></span>
<span class="line"><span style="color:#FFCB6B;">-r(--no-file-warnings</span><span style="color:#EEFFFF;">)：无法打开输入文件时静默；</span></span>
<span class="line"><span style="color:#FFCB6B;">-t：限制页眉,页脚与页尾的填充；</span></span>
<span class="line"><span style="color:#FFCB6B;">-T(--omit-pagination</span><span style="color:#EEFFFF;">)：类似于-t,但也限制换页；</span></span>
<span class="line"><span style="color:#FFCB6B;">-v(--show-non-printing</span><span style="color:#EEFFFF;">)：将不可打印的字符转换为八进制反斜杠格式；</span></span>
<span class="line"><span style="color:#FFCB6B;">-d：双倍行距；</span></span>
<span class="line"><span style="color:#FFCB6B;">-a(--across</span><span style="color:#EEFFFF;">)：交错打印，即水平打印，而非纵向打印；</span></span>
<span class="line"><span style="color:#FFCB6B;">-c(--show-control-chars</span><span style="color:#EEFFFF;">)：将控制字符转换为帽子符号（例如 ^C），将其他不可打印的字符转换为八进制反斜杠格式；</span></span>
<span class="line"><span style="color:#FFCB6B;">--help：打印帮助信息并退出；</span></span>
<span class="line"><span style="color:#FFCB6B;">--version：打印版本信息并退出.</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br></div></div><h3 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-hidden="true">#</a></h3><p>文件：需要转换格式的文件。</p>`,10),r=[e];function o(t,c,F,i,d,b){return a(),n("div",null,r)}const B=s(p,[["render",o]]);export{u as __pageData,B as default};
