import{_ as s,c as n,o as a,a as l}from"./app.82736834.js";const E=JSON.parse('{"title":"ps","description":"","frontmatter":{},"headers":[{"level":2,"title":"补充说明","slug":"补充说明","link":"#补充说明","children":[{"level":3,"title":"语法","slug":"语法","link":"#语法","children":[]},{"level":3,"title":"选项","slug":"选项","link":"#选项","children":[]},{"level":3,"title":"实例","slug":"实例","link":"#实例","children":[]}]}],"relativePath":"linux/command/pages/ps.md","lastUpdated":1666509709000}'),p={name:"linux/command/pages/ps.md"},e=l(`<h1 id="ps" tabindex="-1">ps <a class="header-anchor" href="#ps" aria-hidden="true">#</a></h1><p>报告当前系统的进程状态</p><h2 id="补充说明" tabindex="-1">补充说明 <a class="header-anchor" href="#补充说明" aria-hidden="true">#</a></h2><p><strong>ps命令</strong> 用于报告当前系统的进程状态。可以搭配kill指令随时中断、删除不必要的程序。ps命令是最基本同时也是非常强大的进程查看命令，使用该命令可以确定有哪些进程正在运行和运行的状态、进程是否结束、进程有没有僵死、哪些进程占用了过多的资源等等，总之大部分信息都是可以通过执行该命令得到的。</p><h3 id="语法" tabindex="-1">语法 <a class="header-anchor" href="#语法" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ps(选项</span><span style="color:#EEFFFF;">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="选项" tabindex="-1">选项 <a class="header-anchor" href="#选项" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">-a：显示所有终端机下执行的程序，除了阶段作业领导者之外。</span></span>
<span class="line"><span style="color:#FFCB6B;">a：显示现行终端机下的所有程序，包括其他用户的程序。</span></span>
<span class="line"><span style="color:#FFCB6B;">-A：显示所有程序。</span></span>
<span class="line"><span style="color:#FFCB6B;">-c：显示CLS和PRI栏位。</span></span>
<span class="line"><span style="color:#FFCB6B;">c：列出程序时，显示每个程序真正的指令名称，而不包含路径，选项或常驻服务的标示。</span></span>
<span class="line"><span style="color:#FFCB6B;">-C&lt;指令名称&gt;：指定执行指令的名称，并列出该指令的程序的状况。</span></span>
<span class="line"><span style="color:#FFCB6B;">-d：显示所有程序，但不包括阶段作业领导者的程序。</span></span>
<span class="line"><span style="color:#FFCB6B;">-e：此选项的效果和指定&quot;A&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">e：列出程序时，显示每个程序所使用的环境变量。</span></span>
<span class="line"><span style="color:#FFCB6B;">-f：显示UID,PPIP,C与STIME栏位。</span></span>
<span class="line"><span style="color:#FFCB6B;">f：用ASCII字符显示树状结构，表达程序间的相互关系。</span></span>
<span class="line"><span style="color:#FFCB6B;">-g&lt;群组名称&gt;：此选项的效果和指定&quot;-G&quot;选项相同，当亦能使用阶段作业领导者的名称来指定。</span></span>
<span class="line"><span style="color:#FFCB6B;">g：显示现行终端机下的所有程序，包括群组领导者的程序。</span></span>
<span class="line"><span style="color:#FFCB6B;">-G&lt;群组识别码&gt;：列出属于该群组的程序的状况，也可使用群组名称来指定。</span></span>
<span class="line"><span style="color:#FFCB6B;">h：不显示标题列。</span></span>
<span class="line"><span style="color:#FFCB6B;">-H：显示树状结构，表示程序间的相互关系。</span></span>
<span class="line"><span style="color:#FFCB6B;">-j或j：采用工作控制的格式显示程序状况。</span></span>
<span class="line"><span style="color:#FFCB6B;">-l或l：采用详细的格式来显示程序状况。</span></span>
<span class="line"><span style="color:#FFCB6B;">L：列出栏位的相关信息。</span></span>
<span class="line"><span style="color:#FFCB6B;">-m或m：显示所有的执行绪。</span></span>
<span class="line"><span style="color:#FFCB6B;">n：以数字来表示USER和WCHAN栏位。</span></span>
<span class="line"><span style="color:#FFCB6B;">-N：显示所有的程序，除了执行ps指令终端机下的程序之外。</span></span>
<span class="line"><span style="color:#FFCB6B;">-p&lt;程序识别码&gt;：指定程序识别码，并列出该程序的状况。</span></span>
<span class="line"><span style="color:#FFCB6B;">p&lt;程序识别码&gt;：此选项的效果和指定&quot;-p&quot;选项相同，只在列表格式方面稍有差异。</span></span>
<span class="line"><span style="color:#FFCB6B;">r：只列出现行终端机正在执行中的程序。</span></span>
<span class="line"><span style="color:#FFCB6B;">-s&lt;阶段作业&gt;：指定阶段作业的程序识别码，并列出隶属该阶段作业的程序的状况。</span></span>
<span class="line"><span style="color:#FFCB6B;">s：采用程序信号的格式显示程序状况。</span></span>
<span class="line"><span style="color:#FFCB6B;">S：列出程序时，包括已中断的子程序资料。</span></span>
<span class="line"><span style="color:#FFCB6B;">-t&lt;终端机编号&gt;：指定终端机编号，并列出属于该终端机的程序的状况。</span></span>
<span class="line"><span style="color:#FFCB6B;">t&lt;终端机编号&gt;：此选项的效果和指定&quot;-t&quot;选项相同，只在列表格式方面稍有差异。</span></span>
<span class="line"><span style="color:#FFCB6B;">-T：显示现行终端机下的所有程序。</span></span>
<span class="line"><span style="color:#FFCB6B;">-u&lt;用户识别码&gt;：此选项的效果和指定&quot;-U&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">u：以用户为主的格式来显示程序状况。</span></span>
<span class="line"><span style="color:#FFCB6B;">-U&lt;用户识别码&gt;：列出属于该用户的程序的状况，也可使用用户名称来指定。</span></span>
<span class="line"><span style="color:#FFCB6B;">U&lt;用户名称&gt;：列出属于该用户的程序的状况。</span></span>
<span class="line"><span style="color:#FFCB6B;">v：采用虚拟内存的格式显示程序状况。</span></span>
<span class="line"><span style="color:#FFCB6B;">-V或V：显示版本信息。</span></span>
<span class="line"><span style="color:#FFCB6B;">-w或w：采用宽阔的格式来显示程序状况。</span><span style="color:#EEFFFF;">　</span></span>
<span class="line"><span style="color:#FFCB6B;">x：显示所有程序，不以终端机来区分。</span></span>
<span class="line"><span style="color:#FFCB6B;">X：采用旧式的Linux</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">i386登陆格式显示程序状况。</span></span>
<span class="line"><span style="color:#FFCB6B;">-y：配合选项&quot;-l&quot;使用时，不显示F(flag</span><span style="color:#EEFFFF;">)栏位，并以RSS栏位取代ADDR栏位　。</span></span>
<span class="line"><span style="color:#FFCB6B;">-&lt;程序识别码&gt;：此选项的效果和指定&quot;p&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--cols&lt;每列字符数&gt;：设置每列的最大字符数。</span></span>
<span class="line"><span style="color:#FFCB6B;">--columns&lt;每列字符数&gt;：此选项的效果和指定&quot;--cols&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--cumulative：此选项的效果和指定&quot;S&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--deselect：此选项的效果和指定&quot;-N&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--forest：此选项的效果和指定&quot;f&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--headers：重复显示标题列。</span></span>
<span class="line"><span style="color:#FFCB6B;">--help：在线帮助。</span></span>
<span class="line"><span style="color:#FFCB6B;">--info：显示排错信息。</span></span>
<span class="line"><span style="color:#FFCB6B;">--lines&lt;显示列数&gt;：设置显示画面的列数。</span></span>
<span class="line"><span style="color:#FFCB6B;">--no-headers：此选项的效果和指定&quot;h&quot;选项相同，只在列表格式方面稍有差异。</span></span>
<span class="line"><span style="color:#FFCB6B;">--group&lt;群组名称&gt;：此选项的效果和指定&quot;-G&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--Group&lt;群组识别码&gt;：此选项的效果和指定&quot;-G&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--pid&lt;程序识别码&gt;：此选项的效果和指定&quot;-p&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--rows&lt;显示列数&gt;：此选项的效果和指定&quot;--lines&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--sid&lt;阶段作业&gt;：此选项的效果和指定&quot;-s&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--tty&lt;终端机编号&gt;：此选项的效果和指定&quot;-t&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--user&lt;用户名称&gt;：此选项的效果和指定&quot;-U&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--User&lt;用户识别码&gt;：此选项的效果和指定&quot;-U&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--version：此选项的效果和指定&quot;-V&quot;选项相同。</span></span>
<span class="line"><span style="color:#FFCB6B;">--widty&lt;每列字符数&gt;：此选项的效果和指定&quot;-cols&quot;选项相同。</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br><span class="line-number">29</span><br><span class="line-number">30</span><br><span class="line-number">31</span><br><span class="line-number">32</span><br><span class="line-number">33</span><br><span class="line-number">34</span><br><span class="line-number">35</span><br><span class="line-number">36</span><br><span class="line-number">37</span><br><span class="line-number">38</span><br><span class="line-number">39</span><br><span class="line-number">40</span><br><span class="line-number">41</span><br><span class="line-number">42</span><br><span class="line-number">43</span><br><span class="line-number">44</span><br><span class="line-number">45</span><br><span class="line-number">46</span><br><span class="line-number">47</span><br><span class="line-number">48</span><br><span class="line-number">49</span><br><span class="line-number">50</span><br><span class="line-number">51</span><br><span class="line-number">52</span><br><span class="line-number">53</span><br><span class="line-number">54</span><br><span class="line-number">55</span><br><span class="line-number">56</span><br><span class="line-number">57</span><br><span class="line-number">58</span><br><span class="line-number">59</span><br><span class="line-number">60</span><br><span class="line-number">61</span><br><span class="line-number">62</span><br></div></div><p>由于ps命令能够支持的系统类型相当的多，所以选项多的离谱！</p><h3 id="实例" tabindex="-1">实例 <a class="header-anchor" href="#实例" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">axo</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">pid,comm,pcpu</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 查看进程的PID、名称以及CPU 占用率</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">aux</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">|</span><span style="color:#EEFFFF;"> </span><span style="color:#FFCB6B;">sort</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-rnk</span><span style="color:#EEFFFF;"> </span><span style="color:#F78C6C;">4</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 按内存资源的使用量对进程进行排序</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">aux</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">|</span><span style="color:#EEFFFF;"> </span><span style="color:#FFCB6B;">sort</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-nk</span><span style="color:#EEFFFF;"> </span><span style="color:#F78C6C;">3</span><span style="color:#EEFFFF;">  </span><span style="color:#545454;font-style:italic;"># 按 CPU 资源的使用量对进程进行排序</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-A</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 显示所有进程信息</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-u</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">root</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 显示指定用户信息</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-efL</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 查看线程数</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-e</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">%C : %p :%z : %a</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">|</span><span style="color:#FFCB6B;">sort</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-k5</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-nr</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 查看进程并按内存使用大小排列</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-ef</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 显示所有进程信息，连同命令行</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-ef</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">|</span><span style="color:#EEFFFF;"> </span><span style="color:#FFCB6B;">grep</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">ssh</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># ps 与grep 常用组合用法，查找特定进程</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-C</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">nginx</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 通过名字或命令搜索进程</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">aux</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">--sort=-pcpu,+pmem</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># CPU或者内存进行排序,-降序，+升序</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-f</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">--forest</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-C</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">nginx</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 用树的风格显示进程的层次关系</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">pid,uname,comm</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-C</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">nginx</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 显示一个父进程的子进程</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-e</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">pid,uname=USERNAME,pcpu=CPU_USAGE,pmem,comm</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 重定义标签</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-e</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">pid,comm,etime</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 显示进程运行的时间</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-aux</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">|</span><span style="color:#EEFFFF;"> </span><span style="color:#FFCB6B;">grep</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">named</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 查看named进程详细信息</span></span>
<span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">command</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-p</span><span style="color:#EEFFFF;"> </span><span style="color:#F78C6C;">91730</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">|</span><span style="color:#EEFFFF;"> </span><span style="color:#FFCB6B;">sed</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-n</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">2p</span><span style="color:#EEFFFF;"> </span><span style="color:#545454;font-style:italic;"># 通过进程id获取服务名称</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br></div></div><p>将目前属于您自己这次登入的 PID 与相关信息列示出来</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-l</span></span>
<span class="line"><span style="color:#545454;font-style:italic;">#  UID   PID  PPID        F CPU PRI NI       SZ    RSS WCHAN     S             ADDR TTY           TIME CMD</span></span>
<span class="line"><span style="color:#545454;font-style:italic;">#  501   566   559     4006   0  31  0  4317620    228 -      Ss                  0 ttys001    0:00.05 /App...cOS/iTerm2 --server /usr/bin/login -fpl kenny /Ap...s/MacOS/iTerm2 --launch_shel</span></span>
<span class="line"><span style="color:#545454;font-style:italic;">#  501   592   577     4006   0  31  0  4297048     52 -      S                   0 ttys001    0:00.63 -zsh</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><ul><li>F 代表这个程序的旗标 (flag)， 4 代表使用者为 super user</li><li>S 代表这个程序的状态 (STAT)，关于各 STAT 的意义将在内文介绍</li><li>UID 程序被该 UID 所拥有</li><li>PID 就是这个程序的 ID ！</li><li>PPID 则是其上级父程序的ID</li><li>C CPU 使用的资源百分比</li><li>PRI 这个是 Priority (优先执行序) 的缩写，详细后面介绍</li><li>NI 这个是 Nice 值，在下一小节我们会持续介绍</li><li>ADDR 这个是 kernel function，指出该程序在内存的那个部分。如果是个 running的程序，一般就是 &quot;-&quot;</li><li>SZ 使用掉的内存大小</li><li>WCHAN 目前这个程序是否正在运作当中，若为 - 表示正在运作</li><li>TTY 登入者的终端机位置</li><li>TIME 使用掉的 CPU 时间。</li><li>CMD 所下达的指令为何</li></ul><blockquote><p>在预设的情况下， <code>ps</code> 仅会列出与目前所在的 <code>bash shell</code> 有关的 <code>PID</code> 而已，所以， 当我使用 <code>ps -l</code> 的时候，只有三个 PID。</p></blockquote><p>列出目前所有的正在内存当中的程序</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">aux</span></span>
<span class="line"></span>
<span class="line"><span style="color:#545454;font-style:italic;"># USER               PID  %CPU %MEM      VSZ    RSS   TT  STAT STARTED      TIME COMMAND</span></span>
<span class="line"><span style="color:#545454;font-style:italic;"># kenny             6155  21.3  1.7  7969944 284912   ??  S    二03下午 199:14.14 /Appl...OS/WeChat</span></span>
<span class="line"><span style="color:#545454;font-style:italic;"># kenny              559  20.4  0.8  4963740 138176   ??  S    二03下午  33:28.27 /Appl...S/iTerm2</span></span>
<span class="line"><span style="color:#545454;font-style:italic;"># _windowserver      187  18.0  0.6  7005748  95884   ??  Ss   二03下午 288:44.97 /Syst...Light.WindowServer -daemon</span></span>
<span class="line"><span style="color:#545454;font-style:italic;"># kenny             1408  10.7  2.1  5838592 347348   ??  S    二03下午 138:51.63 /Appl...nts/MacOS/Google Chrome</span></span>
<span class="line"><span style="color:#545454;font-style:italic;"># kenny              327   5.8  0.5  5771984  79452   ??  S    二03下午   2:51.58 /Syst...pp/Contents/MacOS/Finder</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><ul><li>USER：该 process 属于那个使用者账号的</li><li>PID ：该 process 的号码</li><li>%CPU：该 process 使用掉的 CPU 资源百分比</li><li>%MEM：该 process 所占用的物理内存百分比</li><li>VSZ ：该 process 使用掉的虚拟内存量 (Kbytes)</li><li>RSS ：该 process 占用的固定的内存量 (Kbytes)</li><li>TTY ：该 process 是在那个终端机上面运作，若与终端机无关，则显示 ?，另外， tty1-tty6 是本机上面的登入者程序，若为 pts/0 等等的，则表示为由网络连接进主机的程序。</li><li>STAT：该程序目前的状态，主要的状态有</li><li>R ：该程序目前正在运作，或者是可被运作</li><li>S ：该程序目前正在睡眠当中 (可说是 idle 状态)，但可被某些讯号 (signal) 唤醒。</li><li>T ：该程序目前正在侦测或者是停止了</li><li>Z ：该程序应该已经终止，但是其父程序却无法正常的终止他，造成 zombie (疆尸) 程序的状态</li><li>START：该 process 被触发启动的时间</li><li>TIME ：该 process 实际使用 CPU 运作的时间</li><li>COMMAND：该程序的实际指令</li></ul><p>列出类似程序树的程序显示</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-axjf</span></span>
<span class="line"></span>
<span class="line"><span style="color:#545454;font-style:italic;"># USER               PID  PPID  PGID   SESS JOBC STAT   TT       TIME COMMAND            UID   C STIME   TTY</span></span>
<span class="line"><span style="color:#545454;font-style:italic;"># root                 1     0     1      0    0 Ss     ??   10:51.90 /sbin/launchd        0   0 二03下午 ??</span></span>
<span class="line"><span style="color:#545454;font-style:italic;"># root                50     1    50      0    0 Ss     ??    0:10.07 /usr/sbin/syslog     0   0 二03下午 ??</span></span>
<span class="line"><span style="color:#545454;font-style:italic;"># root                51     1    51      0    0 Ss     ??    0:29.90 /usr/libexec/Use     0   0 二03下午 ??</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><p>找出与 cron 与 syslog 这两个服务有关的 PID 号码</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">aux</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">|</span><span style="color:#EEFFFF;"> </span><span style="color:#FFCB6B;">egrep</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">(cron|syslog)</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#545454;font-style:italic;"># root                50   0.0  0.0  4305532   1284   ??  Ss   二03下午   0:10.08 /usr/sbin/syslogd</span></span>
<span class="line"><span style="color:#545454;font-style:italic;"># kenny            90167   0.0  0.0  4258468    184 s007  R+    9:23下午   0:00.00 egrep (cron|syslog)</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p>把所有进程显示出来，并输出到ps001.txt文件</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-darker" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ps</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">-aux</span><span style="color:#EEFFFF;"> </span><span style="color:#89DDFF;">&gt;</span><span style="color:#EEFFFF;"> </span><span style="color:#C3E88D;">ps001.txt</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>输出指定的字段</p>`,25),o=[e];function r(c,t,F,i,y,b){return a(),n("div",null,o)}const C=s(p,[["render",r]]);export{E as __pageData,C as default};
